mapper.select:
select e.*,o.id o_id,o.name org_name,p.id p_id,p.name pos_name from employee_info e left join sys_organization o on e.org_id=o.id left join sys_position p on e.position_id=p.id where 1 = 1
and e.status=#{status}
and org_id=#{orgId}
and number like concat ('%',#{number},'%')
and user_name like concat ('%',#{userName},'%')

mapper.delete:
delete from employee_info
where id in 

#{item.id}
